import React, { useState, useEffect } from "react";
import './style.css';
import Axios from 'axios';
import { Button } from 'react-bootstrap';
import Modal from "react-bootstrap/Modal";
import ModalBody from "react-bootstrap/ModalBody";
import ModalHeader from "react-bootstrap/ModalHeader";
import ModalFooter from "react-bootstrap/ModalFooter";
import ModalTitle from "react-bootstrap/ModalTitle";
function Report() {
  const [show, setShow] = useState(false);
  const [tanggal, setTanggal] = useState('');
  const [id_laporan, setidLaporan] = useState('');
  const [chat_masuk, setChatMasuk] = useState('');
  const [chat_closing, setChatClosing] = useState('');
  const handleClose = () => setShow(false);
  const getByReport = () => setShow(true);
  // const [isi, setIsi] = useState('');
  // const [nama, setNama] = useState('');
  
  // const [todoList, setTodoList] = useState([]);
  const [reportList, setReportList] = useState([]);
  const [reportById, setReportById] = useState([]);
  useEffect(() => {
    Axios.get("http://localhost:3001/api/get/laporanChat").then((response) => {
      setReportList(response.data)
    });
  }, []);

  getByReport = (id_laporan) => {
    Axios.delete(`http://localhost:3001/api/getbyid/laporanChat/${id_laporan}`);
  };
  
  // const addTodo = (id_user) => {
  //   Axios.post("http://localhost:3001/api/insert/todolist", {
  //     isi: isi,
  //     status: status,
  //     id_user: id_user,
  //   });

  //   setTodoList([
  //     ...todoList,
  //     { isi: isi, status: status, id_user: id_user},
  //   ]);
  // };


  const deleteReport = (id_laporan) => {
    Axios.delete(`http://localhost:3001/api/delete/laporanChat/${id_laporan}`);
    window.location.reload(false);
  };



  const updateReport = (id_laporan, tanggal, chat_masuk, chat_closing) => {
    Axios.put("http://localhost:3001/api/update/laporanChat", {
      id_laporan: id_laporan,
      tanggal: tanggal,
      chat_masuk:chat_masuk,
      chat_closing:chat_closing
    });
  };
  
  return (
    <div className="App">
      <h1 id="center"> Laporan Chat </h1> 
      <div className="container">
      <div className="float-right m-4">
      <button className="btn btn-primary">tambah</button>
      </div> 
    <table className="table table-bordered bg-white">
    <thead>
      <tr>
        <th>Nama</th>
        <th>Tanggal</th>
        <th>Chat Masuk</th>
        <th>Chat Closing</th>
        <th>Action</th>
      </tr>
    </thead>
    <tbody>
    {reportList.map((report) => {
      return(
        <tr>
        <td>{report.nama}</td>
        <td>{report.tanggal}</td>
        <td>{report.chat_masuk}</td>
        <td>{report.chat_closing}</td>
        <td width="150">
          <button className="btn btn-primary m-1" onClick={() => {getByReport(report.id_laporan)}}>update</button>
          <button className="btn btn-primary m-1" onClick={() => {deleteReport(report.id_laporan)}}>x</button>
        </td>
      </tr>
      )
       })}
    </tbody>
  </table>
</div>   
      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Update</Modal.Title>
        </Modal.Header>
        <Modal.Body>
        {reportById.map((reportbyid) => {
        return(
        <form action="/">
          {reportbyid.nama}
           <div class="form-group">
            <input className="form-control" type="text" name="id_laporan" placeholder="Tambah User..." onChange={(e) => {
              setidLaporan(e.target.value)
            }}/>
            </div>
            <div class="form-group">
              <label>Tanggal</label>
            <input className="form-control" type="date" name="tanggal" placeholder="Tanggal..." onChange={(e) => {
              setTanggal(e.target.value)
            }} />
            </div>
              <div class="form-group">
                <label>Chat Masuk</label>
            <input className="form-control" type="number" name="chat_masuk" placeholder="Chat Masuk..." onChange={(e) => {
              setChatMasuk(e.target.value)
            }}/>
            </div>
            <div class="form-group">
              <label>Chat CLosing</label>
            <input className="form-control" type="number" name="chat_closing" placeholder="Chat Closing..." onChange={(e) => {
              setChatClosing(e.target.value)
            }} />
            </div>
            </form>
          )})}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}
export default Report;
