  //   });

  //   setTodoList([
  //     ...todoList,
  //     { isi: isi, status: status, id_user: id_user},
  //   ]);
  // };


  const deleteReport = (id_laporan) => {
    Axios.delete(`http://localhost:3001/api/delete/laporanChat/:id_laporan/${id_laporan}`);
  };


  // const updateTodo = (idTodo, status) => {
  //   Axios.put("http://localhost:3001/api/update/todolist", {
  //     id: idTodo,
  //     status: status,
  //   });
  // };
  
  return (
    <div className="App">
      <h1 id="center"> Laporan Chat </h1> 
      <div className="container">         
    <table className="table table-bordered">
    <thead>
      <tr>
        <th>Nama</th>
        <th>Tanggal</th>
        <th>Chat Masuk</th>
        <th>Chat Closing</th>
        <th>Action</th>
      </tr>
    </thead>
    <tbody>
    {reportList.map((report) => {
      return(
        <tr>
        <td>{report.nama}</td>
        <td>{report.tanggal}</td>
        <td>{report.chat_masuk}</td>
        <td>{report.chat_closing}</td>
        <td>
        {/* <a href="/">
          <button className="btn btn-primary" onClick={() => {deleteUser(report.id_laporan)}}>x</button>
        </a>
        <a href="/">
          <button className="btn btn-primary" onClick={() => {deleteUser(report.id_laporan)}}>x</button>
        </a> */}
        <a href="#/report">
          <button className="btn btn-primary" onClick={() => {deleteReport(report.id_laporan)}}>x</button>
        </a>
        </td>
      </tr>
      )
       })}
    </tbody>
  </table>
</div>
    </div>
  );
}
export default Report;
