import React, { useState, useEffect } from "react";
import './App.css';
import Axios from 'axios';

function App() {
  const [id, setId] = useState('');
  const [isi, setIsi] = useState('');
  const [status, setStatus] = useState('belum');
  const [todoList, setTodoList] = useState([]);

  const [newIsi, setNewIsi] = useState('');

  useEffect(() => {
    Axios.get("http://localhost:3001/api/get").then((response) => {
      setTodoList(response.data)
    });
  }, []);

  const submitReview = () => {
    Axios.post("http://localhost:3001/api/insert", {
      isi: isi,
      status: status,
    });

    setTodoList([
      ...todoList,
      { isi: isi, status: status},
    ]);
  };

  const deleteReview = (idTodo) => {
    Axios.delete(`http://localhost:3001/api/delete/${idTodo}`);
  };

  const updateReview = (idTodo) => {
    Axios.put("http://localhost:3001/api/update", {
      id: idTodo,
      isi: newIsi,
      // status: status,
    });
    setNewIsi("");
  };

  return (
    <div className="App">
      <h1 id="center"> To Do List </h1> 
      <div className="form">
        <center>
          <input className="form-control" type="text" name="isi" placeholder="Insert To Do..." onChange={(e) => {
            setIsi(e.target.value)
          }} />
          <br></br>
          <button className="btn btn-primary" onClick={submitReview}>&nbsp; Submit &nbsp;</button>
        </center>
        {todoList.map((val) => {
          return (
            <div className="card">
              <ul class="list-group list-group-flush">
                <li class="list-group-item"><center><b> {val.nama} </b></center></li>
                <li class="list-group-item"><button className="btn btn-primary" onClick={() => {deleteReview(val.id)}}>x</button> &nbsp; belum database</li>
                <li class="list-group-item"><center><b> {val.nama} </b></center></li>
              </ul>
            </div>
          );
        })}
          <div className="card">
            <ul class="list-group list-group-flush">
              <li class="list-group-item"><center><button className="btn btn-primary" onClick={() => {}}>New User</button></center></li>
            </ul>
          </div>
      </div>
    </div>
  );
}

export default App;
