import React, { useState, useEffect } from "react";
import './style.css';
import Axios from 'axios';
import { Button } from 'react-bootstrap';
import Modal from "react-bootstrap/Modal";
import dateFormat from 'dateformat';

class Home extends React.Component {
  constructor(props) {
    super(props)
      this.state = {
        data: [],
        userdetails:[]
              }
              this.addFormData = this.addFormData.bind(this);
      }

      edituser(userid){
        
        const fd = new FormData();
          fd.append('id_user', userid);
          
          
          axios.post('http://localhost:3001/api/update/laporanChat', fd
          ).then(res=>
          {
    
            //Storing user detail in state array object
            this.setState({userdetails: res.data[0]});
            //edit user popup form
            $("#editmodal").modal("show");
          
          }
          );
      }

      render() {
   
        return (
         
          <div className="maincontainer">
          <div class="modal" id="editmodal">
                <div class="modal-dialog">
                  <div class="modal-content">
                  
                    <div class="modal-header">
                      <h4 class="modal-title align-center">User : {this.state.userdetails.username}</h4>
                      <button type="button" class="close" data-dismiss="modal">&times;</button>
                    </div>
                    
                    <div class="modal-body text-center">
                    <form ref={(el) => this.myFormRef = el}>
                    <input type="hidden" id="Username" value={this.state.userdetails.id} ref="myID" />
                      <div className="form-group">
                      <input type="email" className="form-control" id="Email" defaultValue={this.state.userdetails.email} aria-describedby="emailHelp" placeholder="Enter email" ref="myEmail" />
                      
                      </div>
                      <div className="form-group">
                      <input type="text" className="form-control"  id="Username" defaultValue={this.state.userdetails.username} placeholder="Enter Username" ref="myUsername" />
                      </div>
                      <button type="submit" className="btn btn-primary" onClick={this.addFormData}>Update</button>
                    </form>
                    </div>
                  
                    <div class="modal-footer">
                      <button type="button" class="btn btn-danger" data-dismiss="modal">Close</button>
                    </div>
                  </div>
                </div>
              </div>
        
          </div>
    )
    };

      }


    
    

function Report() {
  const [show, setShow] = useState(false);
  const [showTambah, setShowTambah] = useState(false);
  const [tanggal, setTanggal] = useState('');
  const [id_laporan, setidLaporan] = useState('');
  const [chat_masuk, setChatMasuk] = useState('');
  const [chat_closing, setChatClosing] = useState('');
  const [id_user, setIdUser] = useState('');
  const handleClose = () => setShow(false);
  const handleClose2 = () => setShowTambah(false);
  let Open = () => setShow(true);
  const ModalTambah = () => setShowTambah(true);
  
  const [reportList, setReportList] = useState([]);
  const [reportById, setReportById] = useState([]);
  const [userList, setUserList] = useState([]);

  useEffect(() => {
    Axios.get("http://localhost:3001/api/get/user").then((response) => {
      setUserList(response.data)
    });
  }, []);

  useEffect(() => {
    Axios.get("http://localhost:3001/api/get/laporanChat").then((response) => {
      setReportList(response.data)
    });
  }, []);

  useEffect((id_laporan) => {
    Axios.get(`http://localhost:3001/api/getbyid/laporanChat/${id_laporan}`).then((response) => {
      setReportById(response.data)
    });
  }, []);
  
  const addLaporanChat = () => {
    Axios.post("http://localhost:3001/api/insert/laporanChat", {
      tanggal: tanggal,
      chat_masuk: chat_masuk,
      chat_closing:chat_closing,
      id_user: id_user,
    })
    window.location.reload(false);
  };

  const deleteReport = (id_laporan) => {
    Axios.delete(`http://localhost:3001/api/delete/laporanChat/${id_laporan}`);
    window.location.reload(false);
  };

  const updateReport = (id_laporan, tanggal, chat_masuk, chat_closing) => {
    Axios.put("http://localhost:3001/api/update/laporanChat", {
      id_laporan: id_laporan,
      tanggal: tanggal,
      chat_masuk:chat_masuk,
      chat_closing:chat_closing
    });
  };
  
  return (
    <div className="App">
      <h1 id="center"> Laporan Chat </h1> 
      <div className="container">
      <div className="float-right m-4">
      <button className="btn btn-primary" onClick={ModalTambah}>tambah</button>
      </div> 
    <table className="table table-bordered bg-white">
    <thead>
      <tr>
        <th>Nama</th>
        <th>Tanggal</th>
        <th>Chat Masuk</th>
        <th>Chat Closing</th>
        <th>Action</th>
      </tr>
    </thead>
    <tfoot>
    <tr>
        <th>Nama</th>
        <th>Tanggal</th>
        <th>Chat Masuk</th>
        <th>Chat Closing</th>
        <th>Action</th>
      </tr>
    </tfoot>
    <tbody>
    {reportList.map((report) => {
      return(
        <tr>
        <td>{report.nama}</td>
        <td>{report.tanggal}</td>
        <td>{report.chat_masuk}</td>
        <td>{report.chat_closing}</td>
        <td width="150">
          <button className="btn btn-primary m-1" onClick={() => {Open(report.id_laporan)}}>update</button>
          <button className="btn btn-primary m-1" onClick={() => {deleteReport(report.id_laporan)}}>x</button>
        </td>
      </tr>
      )
       })}
    </tbody>
  </table>
</div>   
      <Modal show={showTambah} onHide={handleClose2}>
        <Modal.Header closeButton>
          <Modal.Title>Tambah</Modal.Title>
        </Modal.Header>
        <Modal.Body>
        <label>Nama</label>   
        <select name="id_user" className="form-control" onChange={(e) => {setIdUser(e.target.value)}}>
        {userList.map((user) => {
          return (
            <option value={user.id_user} >{user.nama}</option>          
            );
        })} 
        </select>   

            <div class="form-group">
              <label>Tanggal</label>
            <input className="form-control" type="date" name="tanggal" placeholder="Tanggal..." onChange={(e) => {
              setTanggal(e.target.value)
            }} />
            </div>
              <div class="form-group">
                <label>Chat Masuk</label>
            <input className="form-control" type="number" name="chat_masuk" placeholder="Chat Masuk..." onChange={(e) => {
              setChatMasuk(e.target.value)
            }}/>
            </div>
            <div class="form-group">
              <label>Chat CLosing</label>
            <input className="form-control" type="number" name="chat_closing" placeholder="Chat Closing..." onChange={(e) => {
              setChatClosing(e.target.value)
            }} />
            </div>
         
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose2}>
            Close
          </Button>
          <Button variant="primary" onClick={addLaporanChat}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}
export default Report;
