import React, { useState, useEffect } from "react";
import './style.css';
import Axios from 'axios';

function Report() {
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  // const [isi, setIsi] = useState('');
  // const [nama, setNama] = useState('');
  
  // const [todoList, setTodoList] = useState([]);
  const [reportList, setReportList] = useState([]);
  useEffect(() => {
    Axios.get("http://localhost:3001/api/get/laporanChat").then((response) => {
      setReportList(response.data)
    });
  }, []);

  // const addTodo = (id_user) => {
  //   Axios.post("http://localhost:3001/api/insert/todolist", {
  //     isi: isi,
  //     status: status,
  //     id_user: id_user,
  //   });

  //   setTodoList([
  //     ...todoList,
  //     { isi: isi, status: status, id_user: id_user},
  //   ]);
  // };


  const deleteReport = (id_laporan) => {
    Axios.delete(`http://localhost:3001/api/delete/laporanChat/${id_laporan}`);
    window.location.reload(false);
  };



  const updateReport = (id_laporan, id_user, tanggal, chat_masuk, chat_closing) => {
    Axios.put("http://localhost:3001/api/update/laporanChat", {
      id_laporan: id_laporan,
      id_user: id_user,
      tanggal: tanggal,
      chat_masuk:chat_masuk,
      chat_closing:chat_closing
    });
  };
  
  return (
    <div className="App">
      <h1 id="center"> Laporan Chat </h1> 
      <div className="container">
      <div className="float-right m-4">
      <button className="btn btn-primary">tambah</button>
      </div> 
    <table className="table table-bordered bg-white">
    <thead>
      <tr>
        <th>Nama</th>
        <th>Tanggal</th>
        <th>Chat Masuk</th>
        <th>Chat Closing</th>
        <th>Action</th>
      </tr>
    </thead>
    <tbody>
    {reportList.map((report) => {
      return(
        <tr>
        <td>{report.nama}</td>
        <td>{report.tanggal}</td>
        <td>{report.chat_masuk}</td>
        <td>{report.chat_closing}</td>
        <td width="150">
          <button className="btn btn-primary m-1" onClick={() => {updateReport(report.id_laporan)}}>update</button>
          <button className="btn btn-primary m-1" onClick={() => {deleteReport(report.id_laporan)}}>x</button>
        </td>
      </tr>
      )
       })}
    </tbody>
  </table>
</div>
      <Button variant="primary" onClick={handleShow}>
        Launch demo modal
      </Button>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </div>

  );
}
export default Report;
