import React, { useState, useEffect } from "react";
import './App.css';
import Axios from 'axios';

function App() {
  const [id, setId] = useState('');
  const [isi, setIsi] = useState('');
  const [status, setStatus] = useState('belum');
  const [todoList, setTodoList] = useState([]);

  const [newIsi, setNewIsi] = useState('');

  useEffect(() => {
    Axios.get("http://localhost:3001/api/get").then((response) => {
      setTodoList(response.data)
    });
  }, []);

  const submitReview = () => {
    Axios.post("http://localhost:3001/api/insert", {
      isi: isi,
      status: status,
    });

    setTodoList([
      ...todoList,
      { isi: isi, status: status},
    ]);
  };

  const deleteReview = (idTodo) => {
    Axios.delete(`http://localhost:3001/api/delete/${idTodo}`);
  };

  const updateReview = (idTodo) => {
    Axios.put("http://localhost:3001/api/update", {
      id: idTodo,
      isi: newIsi,
      // status: status,
    });
    setNewIsi("");
  };

  return (
    <div className="App">
      <h1 style="text-align:center"> To Do List </h1> 
      <div className="form">
        <input type="text" name="isi" placeholder="Insert To Do..." onChange={(e) => {
          setIsi(e.target.value)
        }} /> &nbsp;
        <button onClick={submitReview}>&nbsp; Submit &nbsp;</button>

        {todoList.map((val) => {
          return (
            <div className="card">
              <div>
                <br></br> 
                {val.nama}
                <button className="btn btn-primary" onClick={() => {deleteReview(val.id)}}>X</button>
                {/* {val.id} */}
              </div>
              &nbsp;
              {/* <input type="text" id="updateInput" onChange={(e) => {
                setNewIsi(e.target.value)
              }}/> */}
              &nbsp;
              {/* <button onClick={() => {updateReview(val.id)}}>Update</button> */}
            </div>
          );
        })}

      </div>
    </div>
  );
}

export default App;
