import React, { useState, useEffect } from "react";
import './App.css';
import Axios from 'axios';

function App() {
  const [isi, setIsi] = useState('');
  const [status, setStatus] = useState('belum')
  const [id_user, setId_user] = useState('');
  const [nama, setNama] = useState('');

  const [todoList, setTodoList] = useState([]);
  const [userList, setUserList] = useState([]);

  const [newStatus, setNewStatus] = useState('sudah');

  useEffect(() => {
    Axios.get("http://localhost:3001/api/get/user").then((response) => {
      setUserList(response.data)
    });
  }, []);

  useEffect(() => {
    const handleId_user = () => document.getElementById("id_user");
    Axios.get(`http://localhost:3001/api/get/todolist/${id_user}`).then((response) => {
      setTodoList(response.data)
    });
  }, []);


  const addTodo = () => {
    Axios.post("http://localhost:3001/api/insert/todolist", {
      isi: isi,
      status: status,
      id_user: id_user,
    });

    setTodoList([
      ...todoList,
      { isi: isi, status: status, id_user: id_user},
    ]);
  };

  const addUser = () => {
    Axios.post("http://localhost:3001/api/insert/user", {
      nama: nama,
    });

    setUserList([
      ...userList,
      {nama: nama},
    ]);
  };

  const deleteTodo = (idTodo) => {
    Axios.delete(`http://localhost:3001/api/delete/todolist/${idTodo}`);
  };

  const deleteUser = (id_user) => {
    Axios.delete(`http://localhost:3001/api/delete/user/${id_user}`);
  };

  const updateTodo = (idTodo) => {
    Axios.put("http://localhost:3001/api/update/todolist", {
      id: idTodo,
      status: newStatus,
    });
    setNewStatus("");
  };

  return (
    <div className="App">
      <br></br>
      <h1 id="center"> TO DO LIST </h1> 
      <div className="card" id="set">
        <ul class="list-group list-group-flush">
          <li class="list-group-item">
            <br></br>
            <input className="form-control" type="text" name="nama" placeholder="Tambah User..." onChange={(e) => {
              setNama(e.target.value)
            }} />
            <br></br>
            <center><button className="btn btn-primary" onClick={addUser}>New User</button></center></li>
        </ul>
      </div>
      <br></br>

      <div className="row">
        {userList.map((user) => {
          return (
            // <form onSubmit="">
            <div class="col-sm-4">
            <form className="card">
              <ul class="list-group list-group-flush">
                <li class="list-group-item">
                  <div class="row">
                    <div class="col-6 col-md-4"></div>
                    <div class="col-6 col-md-4"><center><b>{user.nama}</b></center></div>
                    <div class="col-6 col-md-4">
                      <div class="row">
                        <div class="col-sm"></div>
                        <div class="col-sm"></div>
                        <div class="col-sm"><button className="btn btn-primary" onClick={() => {deleteUser(user.id_user)}}>x</button></div>
                      </div>
                    </div>
                  </div>
                </li>
                <input type="hidden" name="id_user" id="id_user" value={user.id_user} />
                {todoList.map((todo) => {
                return (
                <li class="list-group-item">
                  <div class="row">
                    <div class="col-sm-2">
                      <button className="btn btn-primary" onClick={() => {deleteTodo(user.id_user)}}>x</button>
                    </div>
                    <div class="col-sm-10">
                      {todo.isi}
                    </div>
                  </div>
                </li>
                  );
                })} 
                <li class="list-group-item">
                  <div class="row">
                    <div class="col-sm-8">
                      <input className="form-control" type="text" name="isi" placeholder="Tambah To Do..." onChange={(e) => {
                        setIsi(e.target.value)
                      }} />
                    </div>
                    <div class="col-sm-2">
                      <button className="btn btn-primary" onClick={addTodo}>Tambah</button>
                    </div>
                  </div>
                </li>
              </ul>
            </form>
          </div>
          // </form>
          );
        })}
        </div>
        <br></br>
    </div>
  );
}

export default App;
